import React, {createContext, useContext, useEffect} from 'react';
import { useState } from "react";
import axios from "axios";
import Profile from "../profile/profile";
import TextComp from "../fieldComponents/textComp";
import {FormComponent} from "../formComponents/FormComponent";
import Input from "../formComponents/Input";
import TextArea from "../fieldComponents/textArea";
import {FormProps} from "../interfaces/FormProps";
import {toast} from "react-toastify";
import {useParams} from "react-router-dom";
import SelectOption from "../inputComponents/selectOption";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import "./roomStyle.sass";
import {ToggleButton} from "react-bootstrap";
// import "bootstrap4-toggle";
// import "bootstrap";
interface Entity {
    id: string;
    number: string;
    price: number;
    size: number;
    createdDate: Date;
    updatedDate:Date;
    main_image: string;
    type: {
        roleId: string;
        name: string;
    }
    facilities: {
        name: string;
        icon: string;
        id: number;
    }[];
}

interface Facility {
    id: string;
    name: string;
}

const initialState= {
    number: "",
    price: 0,
    size: 0,
    createdDate: new Date(),
    updatedDate: new Date(),
    main_image: "",
    typeId: "",
    facilitiesIds: [],
};




    interface Room {
    data: any;
}

export const RoomForm:React.FC<FormProps> = ({ selectedId , onSave})  => {
    const {number, price, size, createdDate, updatedDate, main_image, typeId, facilitiesIds} = initialState;
    const [state, setState] = useState(initialState);

    const [typeList, setTypeList] = useState([{"name": " ", "id":""}]);

    useEffect(() => {

        const getRoom = async () => {
            const response = await axios.get(`http://localhost:3300/rooms/${selectedId}`);
            if(response.status === 200){
                console.log(response.data);
                setState(response.data);
                // setData(response.data);
            }
        }

        if (selectedId !== null) {
            getRoom();
        }
    }, [selectedId]);


    useEffect(() => {
        axios.get('http://localhost:3300/types')
            .then(response => setTypeList(response.data))
            .catch(error => console.log(error));
    }, []);


    // {number: string; price: number; size:number; createdDate:Date, updatedDate:Date, main_image:string, typeId:string; facilitiesIds:[]}
    const addRoom = async (data: { number: string; createdDate: Date; size: number; facilitiesIds: any[]; price: number; main_image: string; typeId: string; updatedDate: Date }) => {
        console.log(data);
        const response = await axios.post("http://localhost:3300/rooms", data);
        toast.success(response.data);
    }

    const updateRoom = async (data: { number: string; createdDate: Date; size: number; facilitiesIds: any[]; price: number; main_image: string; typeId: string; updatedDate: Date }, id: string | null) => {
        console.log(data);
        const response = await axios.put(`http://localhost:3300/rooms/${id}`, data);
        toast.success(response.data);
    }

    const getSingleRoom = async (id: string | null) => {
        const response = await axios.get(`http://localhost:3300/rooms/${id}`);
        if(response.status === 200){
            toast.success(response.data);
            setState({...response.data[0]});
        }
    }

    const {id} = useParams();
    useEffect(() => {
        if(id){
            getSingleRoom(id);
        }
    }, [id])


    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        console.log(state);
        if(!state){
            alert("empty fields");
            toast.error("Do not leave blank fields");
        } else{
            if(!selectedId){
                addRoom(state)
            }else{
                console.log(state);
                updateRoom(state, selectedId)
                console.log(state);
            }

        }
        window.location.reload()
    };

    const handleChange = (event: React.ChangeEvent<HTMLInputElement> | React.ChangeEvent<HTMLTextAreaElement> | React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        const { name, value } = event.target;
        setState({ ...state, [name]: value });
    };


    return (
        <div>
            <link href="https://cdn.jsdelivr.net/gh/gitbrent/bootstrap4-toggle@3.6.1/css/bootstrap4-toggle.min.css" rel="stylesheet"/>
            <script src="https://cdn.jsdelivr.net/gh/gitbrent/bootstrap4-toggle@3.6.1/js/bootstrap4-toggle.min.js"></script>
            <div className={"div-container"}>
                <h1>{selectedId}</h1>
                <TextComp text={selectedId === null ? 'Add Room' : 'Edit Room'} />
                <FormComponent onSubmit={handleSubmit} buttonText={selectedId === null ? 'Add Room' : 'Edit Room'}>

                    <div className="select-box border bg-white">

                            <div className={"row"}>
                                <div className={"col"}>
                                    <Input className={"name"} name="name" label="Name" value={state.number} onChange={handleChange} />
                                </div>
                                <div className={"col"}>
                                    <input type="checkbox" checked data-toggle="toggle" data-onstyle="outline-secondary" data-offstyle="outline-success"/>
                                    <ToggleButton value={"on"}/>
                                </div>
                            </div>


                        <div className="text-muted pl-1">Room Types</div>

                        <SelectOption name={"typeId"} value={state.typeId} onChange={handleChange} entityList={typeList}  className={""}/>
                        {/*<SelectOption  entityList={typeList}/>*/}
                    </div>
                    {/*<SelectOption name={"types"} value={state.typeId} onChange={handleChange} entityList={typeList} text={"Room Types"}/>*/}
                </FormComponent>

            </div>
        </div>

    );
};
