import React from "react";
import "./avatar.sass";

interface AvatarProps {
    name: string;
}
const Avatar: React.FC<AvatarProps> = ({ name }) => {
    const [initials] = name.split(" ").map(part => part[0].toUpperCase());
    return <div className={"avatar"}>{initials}</div>;
};

export default Avatar;
