import React, {useEffect, useState} from 'react';

import "./form.sass";

import TextComp from "../fieldComponents/textComp";
import { connect } from 'react-redux';
import { addItem, getItems, updateItem, deleteItem } from "../../action";
import {Item} from "../../store/item";
import axios from "axios";
import { toast } from "react-toastify";
import {useParams} from "react-router-dom";
import {getRoles} from "@testing-library/react";
import Profile from "../profile/profile";
// import { useHistory } from 'react-router-dom';

// interface Props {
//     items: Item[];
//     addItem: typeof addItem;
//     getItems: typeof getItems;
//     updateItem: typeof updateItem;
//     deleteItem: typeof deleteItem;
// }

const initialState={
    name: "",
    description: ""
}

interface RoleFormProps {
    selectedRoleId: string | null;
    onSave: () => void;
}

interface Entity {
    name: string;
    description: string;
}


const MyForm: React.FC<RoleFormProps> = ({ selectedRoleId , onSave})  => {

    const [state, setState] = useState(initialState);
    const {name, description} = initialState;
    const [data, setData] = useState<Entity>(initialState);
    const [role, setRole] = useState({ name: '', description: '' });


    // const setRole = async () => {
    //     const response = await axios.put(`http://localhost:3300/roles/${selectedRoleId}`);
    //     if(response.status === 200){
    //         setData(response.data);
    //     }
    // }

    useEffect(() => {

        const getRole = async () => {
            const response = await axios.get(`http://localhost:3300/roles/${selectedRoleId}`);
            if(response.status === 200){
                console.log(response.data);
                setRole(response.data);
                // setData(response.data);
            }
        }

        if (selectedRoleId !== null) {
            getRole();
        }
    }, [selectedRoleId]);

    // useEffect(() => {
    //     if (selectedRoleId) {
    //         const role = roles.find((role) => role.id === selectedRoleId);
    //         setState(role ? role.name : '', description ? role.description : '');
    //     } else {
    //         setState('');
    //     }
    // }, [selectedRoleId]);

    // const navigate = useNavigate();
    const addRole = async (data: { name: string; description: string; }) => {
        console.log(data);
        const response = await axios.post("http://localhost:3300/roles", data);
        toast.success(response.data);
    }

    const updateRole = async (data: { name: string; description: string; }, id: string | null) => {
        console.log(data);
        const response = await axios.put(`http://localhost:3300/roles/${id}`, data);
        toast.success(response.data);
    }

    const getSingleRole = async (id: any) => {
        const response = await axios.get(`http://localhost:3300/roles/${id}`);
        if(response.status === 200){
            toast.success(response.data);
            setState({...response.data[0]});
        }
    }


    const {id} = useParams();
    useEffect(() => {
        if(id){
            getSingleRole(id);
        }
    }, [id])


    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        // onSave(role);
        if(name.trim() === "" || description.trim() === ""){
            alert("empty fields");
            toast.error("Do not leave blank fields");
        } else{
                addRole(state);

            if(!id){
                alert("add");
                addRole(state);
            }else{
                alert("edit");
            }
                updateRole(state, selectedRoleId);
            }

            window.location.reload()
            // navigate("/");
        // }
    };

    // const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    //     const {name, value } = event.target;
    //     setState({...state, [name]:value})
    // }

    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        const { name, value } = event.target;
        setRole({ ...state, [name]: value });
    };

    // const handleChangeTextArea = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    //     const {name, value } = event.target;
    //     setState({...state, [name]:value})
    // };
    //
    // const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    //     setRole({ ...role, [event.target.name]: event.target.value });
    // };

    // const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    //     event.preventDefault();
    //     onSave(role);
    // };

    return (
        <div>
            <Profile/>
            <div className={"div-container"}>
                <h1>{selectedRoleId}</h1>
                <TextComp text={selectedRoleId === null ? 'Add Role' : 'Edit Role'} />
                <form className={"form"}
                      onSubmit={handleSubmit}
                >
                    <div className="form-group">
                        <label htmlFor={"name"}>Name</label><br/>
                        <input  name="name" id={"name"}
                                type={"text"}
                                placeholder={"Enter name..."}
                                value={role.name}
                                onChange={handleInputChange} /><br/>
                    </div>
                    <div className="form-group">
                        <label htmlFor={"description"}>Description</label><br/>
                        <textarea className={"textarea"}
                                  id={"description"}
                                  name={"description"}
                                  placeholder={"Description..."}
                                  value={role.description}
                                  onChange={handleInputChange} /><br/>
                    </div>
                    <input type={"submit"} value={selectedRoleId === null ? 'Add Role' : 'Edit Role'}/>
                </form>
            </div>
        </div>

    );
};

export default MyForm;
