import React from "react";
import {Form, Formik} from "formik";
import {useSignupUserMutation} from "../../store/api/authAPi";
import {Grid, Heading, Stack} from "@chakra-ui/react";
import {InputControl, SubmitButton} from "formik-chakra-ui";
import * as Yup from "yup";
import "./signup.style.sass"
const Signup = () => {

    const [signupUser, {data, isLoading }] = useSignupUserMutation();
    console.log(data);

    return (
        <div className="container">
            <div className="card">
                <div className="card-image">
                    <h2 className="card-heading">
                        Premium Hotel
                        <small>Signin</small>
                    </h2>
                </div>
        <Formik
            initialValues={{firstname:"", lastname:"", email: "", password: "" }}

            onSubmit={(values) => {
                signupUser({ ...values });
                // console.log(values);
                // setEmail(values.email);
            }}

            validationSchema={Yup.object({
                    firstname: Yup.string()
                        .required("Firstname is required"),
                    lastname: Yup.string()
                        .required("Lastname is required"),
                    email: Yup.string()
                        .email("Invalid email address")
                        .required("Email address is required"),
                    password: Yup.string()
                        .required("Password is required"),
                }
            )}
        >
            <Form>
                <Stack boxShadow="xl" borderRadius="md">
                        <InputControl
                            className={"input-field"}
                            name="firstname"
                            label="Firstname"
                            inputProps={{
                                type: "text",
                                placeholder: "Enter Firstname...",
                            }}
                        />
                        <InputControl
                            className={"input-field"}
                            name="lastname"
                            label="Lastname"
                            inputProps={{
                                type: "test",
                                placeholder: "Enter Lastname...",
                            }}
                        />
                        <InputControl
                            name="email"
                            label="Email"
                            inputProps={{
                                type: "email",
                                placeholder: "Enter Email...",
                            }}
                        />
                        <InputControl
                            className={"input-field"}
                            name="password"
                            label="Password"
                            inputProps={{
                                placeholder: "Enter Password...",
                                type: "password",
                            }}
                        />

                        <SubmitButton className="action" isLoading={isLoading}>Signup</SubmitButton>
                    </Stack>
            </Form>
        </Formik>
            </div>
        </div>
    );
};

export default Signup;
