import Profile from "../profile/profile";
import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import "../formComponents/ListAllstyle.sass";
import axios from "axios";
import {toast} from "react-toastify";
import "./roomStyle.sass";

interface Entity {
    id: string;
    number: string;
    price: number;
    size: number;
    createdDate: Date;
    updatedDate:Date;
    main_image: string;
    type: {
        roleId: string;
        name: string;
    }
    facilities: Facility[];
}

interface Facility {
    id: string;
    name: string;
}

interface ListProps {
    onEdit: (id: string) => void;
}

export const EntityList = ({ onEdit }: ListProps) => {

    const [data, setData] = useState<Entity[]>([]);
    const [editingItemId, setEditingItemId] = useState<string | null>(null);
    const [typeList, setTypeList] = useState([{"name": " ", "id":""}]);


    const onEditItem = (id: string) => {
        setEditingItemId(id);
    };

    useEffect(() => {
        getRooms();
    }, []);

    const getRooms = async () => {
        const response = await axios.get("http://localhost:3300/rooms");
        if(response.status === 200){
            setData(response.data);
            console.log(response.data);
        }
    }

    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        const { name, value } = event.target;
        setData({ ...data, [name]: value });
    };


    useEffect(() => {
        axios.get('http://localhost:3300/types')
            .then(response => setTypeList(response.data))
            .catch(error => console.log(error));
    }, []);

    const onDeleteRoom = async (id: string) => {
        if(window.confirm("Are you sure you want to delete this room")){
            const response = await axios.delete(`http://localhost:3300/rooms/${id}`);
            if(response.status === 200){
                toast.success(response.data);
                getRooms();
            }
        }
    }

    console.log("data " + data);

    const [editId, setEditId] = useState<string | null>(null);

    const handleEdit = (id: string) => {
        setEditId(id);
    };
    // <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.2.1/jquery.min.js"></script>

    return (

        <div className="wrapper bg-white border">
             <div className="h2 font-weight-bold text-center mt-3">Added 5 Photos </div>
            {data && data.map((entity) => (
                <div id="boxes" key={entity.id}>
                    <div className="box">
                        <div className="row">
                            <div className="col-sm-4">
                                <img
                                    src="https://images.pexels.com/photos/5998132/pexels-photo-5998132.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
                                    alt=""/>
                            </div>
                            <div className="col-sm-8">
                                <div className="select-box border bg-white">
                                    <div className="text-muted pl-1">Tag</div>
                                    <select>
                                        <option value="honeymoon">Bedroom</option>
                                        <option value="wedding">Waiting Hall</option>
                                        <option value="guest">Hall</option>
                                        <option value="guest">Kitchen</option>
                                    </select>
                                </div>
                                <label className="option">Make Primary Photo
                                    <input type="checkbox" checked/>
                                        <span className="checkmark"></span>
                                </label>
                            </div>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    )
}
