import React, {useEffect, useState} from 'react';
import "./signup.sass";
import axios from "axios";
import {toast} from "react-toastify";
import Dropdown from "../../components/dropdownComp/dropdown";




const initialState={
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    roleId: 0
}

interface Entity {
    id: number;
    name: string;
    description: string;
}

const Signup = () => {

    const [state, setState] = useState(initialState);
    const { firstname, lastname, email, password } = initialState;
    const [roleId, setRoleId] = useState("");
    const [roleList, setRoleList] = useState([{"name": " ", "id":""}]);

    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const {name, value } = event.target;
        setState({...state, [name]:value})
    }


    // const [entities, setEntities] = useState<Entity[]>([]);
    // const [selectedEntity, setSelectedEntity] = useState<Entity | null>(null);


    // useEffect(() => {
    //     axios.get('http://localhost:3300/roles').then(res => {
    //         setEntities(res.data);
    //         console.log(res.data);
    //     });
    // }, []);

    useEffect(() => {
        const fetchData = async ()=>{
            const response = await axios.get("http://localhost:3300/roles");
            if(response.status === 200){
                setRoleList(response.data);
                console.log(response.data);
            }
            };
        fetchData();
    }, []);

    const handleSelectChange = (event: { target: { value: React.SetStateAction<string>; }; }) =>{
        setRoleId(event.target.value);
        console.log(event.target.value);
    }



    const addUser = async (data: {firstname: string; lastname: string; email:string; password:string; roleId: number}) => {
        console.log(data);
        const response = await axios.post("http://localhost:3300/signup", data);
        toast.success(response.data);
    }

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        console.log(roleId);
        addUser(state)
        // setSuccess(true);
        // window.location.reload()

    };




    // const handleSelect = (selected: Entity) => {
    //     console.log(selected);
    // };

    return (
      <div className="container-signup">
          {/*<p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>*/}
          <div className="card-signup">
              <div className="card-image">
                  <h2 className="card-heading">
                      Royal Hotel
                      <small>Royal Hotel</small>
                  </h2>
              </div>
              <form onSubmit={handleSubmit}>
                  <div className="card-form">
                      <div className="column">
                          <div className="input">
                              <input type="text" className="input-field"
                                     onChange={handleInputChange}
                                     name={"firstname"}
                                     value={state.firstname}
                                     required/>
                              <label className="input-label">Name</label>
                          </div>
                          <div className="input">
                              <input type="text" className="input-field"
                                     name={"email"}
                                     value={state.email}
                                     onChange={handleInputChange}
                                     required/>
                              <label className="input-label">Email</label>
                          </div>
                          <div className="input">
                              <input type="password" className="input-field"
                                     name={"password"}
                                     value={state.password}
                                     onChange={handleInputChange}
                                     required/>
                              <label className="input-label">Password</label>
                          </div>
                      </div>
                      <div className={"column"}>
                          <div className="input">
                              <input type="text" className="input-field"
                                     name={"lastname"}
                                     value={state.lastname}
                                     onChange={handleInputChange}
                                     required/>
                              <label className="input-label">Lastname</label>
                          </div>
                          <div className={"input"}>
                              <select className={"form-control-select"} value={roleId} onChange={handleSelectChange}>
                                  <option value={""}>Choose User Role</option>
                                  {roleList.map(role => (
                                      <option key={role.id} value={role.id}>{role.name}</option>
                                  ))}
                              </select>
                             {/*<Dropdown entity={entities}/>*/}
                             {/* <Dropdown entities={entities} selectedEntity={selectedEntity} setSelectedEntity={setSelectedEntity} />*/}
                          </div>
                          <div className="input">
                              <input type="password" className="input-field"
                                     required/>
                              <label className="input-label">Confirm Password</label>
                          </div>
                      </div>


                  </div>
                      <div className="action">
                          <button  className="action-button">Get started</button>
                          {/*disabled={!validName || !validPwd || !validMatch ? true : false}*/}
                      </div>


              </form>
              <div className="card-info">
                  <p>By signing up you are agreeing to our <a href="#">Terms and Conditions</a></p>
              </div>
          </div>
      </div>

  );
};

export default Signup;
