import React, { useState, useEffect, useRef } from "react";
import Glide from '@glidejs/glide'
import "./glideStyle.sass";
import axios from "axios";

interface Entity {
    id: string;
    number: string;
    price: number;
    size: number;
    availability: boolean;
    reserved:Date;
    main_image: string;
    type: {
        roleId: string;
        name: string;
    }
    facilities: Facility[];
}

interface Facility {
    id: string;
    name: string;
}


export const ImagesGlide:React.FC = ()  => {
    const [currentSlide, setCurrentSlide] = useState(0);
    // const sliderRef = useRef<HTMLDivElement>(null);
    const [rooms, setRooms] = useState<Entity[]>([]);

    useEffect(() => {
        getRooms();
    }, []);

    const getRooms = async () => {
        const response = await axios.get("http://localhost:3300/rooms");
        if(response.status === 200){
            setRooms(response.data);
            console.log(response.data);
        }
    }


    const sliderRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        const glide = new Glide(sliderRef.current, {
            type: 'carousel',
            perView: 3,
            focusAt: 'center',
            gap: 40,
            autoplay: 2000,
            breakpoints: {
                1200:{
                    perView: 3
                },
                800:{
                    perView: 2
                }
            }
        });
        glide.mount();
        return () => {
            glide.destroy();
        };
    }, []);





    return (
        <div className="images" ref={sliderRef}>
            <div className="glide__track" data-glide-el="track">
                <ul className="glide__slides">
                    {rooms.map((room) => (
                        <li className="glide__slide">
                            <img
                                src={`http://localhost:5500/images/uploaded_images/${room.main_image}`}
                                alt=""
                            />
                        </li>
                    ))}
                </ul>
            </div>

            <div className="glide__arrows" data-glide-el="controls">
                <button className="glide__arrow glide__arrow--left" data-glide-dir="<">
                    <i className="fas fa-arrow-left" />
                </button>
                <button className="glide__arrow glide__arrow--right" data-glide-dir=">">
                    <i className="fas fa-arrow-right" />
                </button>
            </div>
        </div>
    );
}
