import React, {createContext, useContext, useEffect} from 'react';
import { useState } from "react";
import axios from "axios";
import Profile from "../profile/profile";
import TextComp from "../fieldComponents/textComp";
import {FormComponent} from "../formComponents/FormComponent";
import Input from "../formComponents/Input";
import TextArea from "../fieldComponents/textArea";
import {FormProps} from "../interfaces/FormProps";
import {toast} from "react-toastify";
import {useParams} from "react-router-dom";
import SelectOption from "../inputComponents/selectOption";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
// import "./roomStyle.sass";
import {ToggleButton} from "../toggleButton/ToggleButton";
import {CheckboxList} from "../CheckBox/checkbox";
import {MultiSelection} from "../multiSelection/MultiSelection";
import {ImageUpload} from "../imageUpload/ImageUpload";

interface Entity {
    id: string;
    number: string;
    price: number;
    size: number;
    createdDate: Date;
    updatedDate:Date;
    main_image: string;
    type: {
        roleId: string;
        name: string;
    }
    facilities: {
        name: string;
        icon: string;
        id: number;
    }[];
}

interface Facility {
    id: string;
    name: string;
}

const initialState= {
    number: "",
    price: 0,
    size: 0,
    availability: "",
    reserved: "",
    createdDate: new Date(),
    updatedDate: new Date(),
    main_image: "",
    typeId: "",
    facilitiesIds: [],
};




    interface Room {
    data: any;
}

export const RoomForm:React.FC<FormProps> = ({ selectedId , onSave})  => {
    const {number, price, size, createdDate, updatedDate, main_image, typeId, facilitiesIds, availability, reserved} = initialState;
    const [state, setState] = useState(initialState);
    const [isAvailable, setIsAvailable] = useState(false);

    const [typeList, setTypeList] = useState([{"name": " ", "id":""}]);
    const [facilitiesList, setFacilityList] = useState([{"name": " ", "id":""}]);
    const [isButtonEnabled, setIsButtonEnabled] = useState(false);
    const [isChecked, setIsChecked] = useState(false);

    useEffect(() => {

        const getRoom = async () => {
            const response = await axios.get(`http://localhost:3300/rooms/${selectedId}`);
            if(response.status === 200){
                console.log(response.data);
                setState(response.data);
                // setData(response.data);
            }
        }

        if (selectedId !== null) {
            getRoom();
        }
    }, [selectedId]);


    useEffect(() => {
        axios.get('http://localhost:3300/types')
            .then(response => setTypeList(response.data))
            .catch(error => console.log(error));
    }, []);

    useEffect(() => {
        axios.get('http://localhost:3300/facilities')
            .then(response => setFacilityList(response.data))
            .catch(error => console.log(error));
    }, []);


    // {number: string; price: number; size:number; createdDate:Date, updatedDate:Date, main_image:string, typeId:string; facilitiesIds:[]}
    const addRoom = async (data: { number: string;  size: number; facilitiesIds: any[]; price: number; main_image: string; typeId: string; availability: string; reserved: string; }) => {
        // console.log(data);
        const response = await axios.post("http://localhost:3300/rooms", data);
        toast.success(response.data);
    }

    const updateRoom = async (data: { number: string; size: number; facilitiesIds: any[]; price: number; main_image: string; typeId: string; availability: string; reserved: string; }, id: string | null) => {
        // console.log(data);
        const response = await axios.put(`http://localhost:3300/rooms/${id}`, data);
        toast.success(response.data);
    }

    const getSingleRoom = async (id: string | null) => {
        const response = await axios.get(`http://localhost:3300/rooms/${id}`);
        if(response.status === 200){
            toast.success(response.data);
            setState({...response.data[0]});
        }
    }

    const {id} = useParams();
    useEffect(() => {
        if(id){
            getSingleRoom(id);
        }
    }, [id])


    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        console.log("test")
        // console.log(state);
        console.log("number: " + state.number + " size: " + state.size + " price: " + state.price + " image: " +
             " type: " + state.typeId + " facilities: " + state.facilitiesIds + " av : " + state.availability + " res : " + state.reserved);

        // if(!state){
        //     alert("empty fields");
        //     toast.error("Do not leave blank fields");
        // }
        // else{
        //     if(!selectedId){
        //         addRoom(state)
        //     }else{
        //         // console.log(state);
        //         updateRoom(state, selectedId)
        //         // console.log(state);
        //     }

        // }
        // window.location.reload()
    };

    const handleChange = (event: React.ChangeEvent<HTMLInputElement> | React.ChangeEvent<HTMLTextAreaElement> | React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        const { name, value } = event.target;
        setState({ ...state, [name]: value });
    };

    const handleImageUpload = (file: File) => {
        const reader = new FileReader();
        reader.onload = () => {
            const dataUrl = reader.result as string;
            setState({ ...state, main_image: dataUrl });
        };
        reader.readAsDataURL(file);
    };

    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setIsChecked(event.target.checked);
        onChange(event);
    }


    const handleToggleButtonClick = () => {
        setIsButtonEnabled((prevState) => !prevState);
    };

    return (
        <div>
            <div className={"div-container"}>
                <h1>{selectedId}</h1>
                <TextComp text={selectedId === null ? 'Add Room' : 'Edit Room'} />
                <FormComponent onSubmit={handleSubmit} buttonText={selectedId === null ? 'Add Room' : 'Edit Room'}>

                    <div className="select-box border bg-white">
                            <div className={"row"}>
                                <div className={"col"}>
                                    <Input className={"name"} name="number" label="Room Number" value={state.number} onChange={handleChange} />
                                </div>
                                <div className={"col"}>
                                    <div className="form-group">
                                        <label htmlFor={"typeId"}></label><br/>
                                            <div className="text-muted pl-1">Room Type</div>
                                            <SelectOption name={"typeId"} value={state.typeId} onChange={handleChange} entityList={typeList}  className={""}/>
                                    </div>
                                </div>
                            </div>
                            <div className={"row"}>
                                <div className={"col"}>
                                    <Input className={"name"} name="price" label="Price" value={state.price} onChange={handleChange} />
                                </div>
                                <div className={"col"}>
                                    <Input className={"name"} name="size" label="Size" value={state.size} onChange={handleChange} />
                                </div>
                            </div>
                            <div className={"row"}>
                                <div className={"col"}>
                                    <ToggleButton name={"availability"} label={"Availability"}    onChange={handleToggleButtonClick} isChecked={isButtonEnabled}/>
                                </div>
                                <div className={"col"}>
                                    <ToggleButton name={"reserved"} label={"Reserved"}    onChange={handleToggleButtonClick} isChecked={isButtonEnabled} />
                                </div>
                            </div>
                        <div className={"row mt-5"}>
                            <div className={"col"}>
                                <ImageUpload onUpload={handleImageUpload} />
                            </div>
                            <div className={"col"}>
                                <p>Facilties</p>
                                <MultiSelection entityList={facilitiesList}/>
                            </div>
                        </div>
                    </div>
                </FormComponent>

            </div>
        </div>

    );
};
