import React, {FC, ReactElement, useEffect, useState} from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import Datetime from 'react-datetime-picker';
import 'react-datetime-picker/dist/entry.nostyle';
import DateTimePicker from "react-datetime-picker";
import axios from "axios";


interface Entity {
    id: string;
    number: string;
    price: number;
    size: number;
    availability: boolean;
    reserved:Date;
    main_image: string;
    type: {
        roleId: string;
        name: string;
    }
    facilities: Facility[];
}

interface Facility {
    id: string;
    name: string;
}
export const Rooms: FC = (): ReactElement =>{

    const [data, setData] = useState<Entity[]>([]);

    useEffect(() => {
        getRooms();
    }, []);

    const getRooms = async () => {
        const response = await axios.get("http://localhost:3300/rooms");
        if(response.status === 200){
            setData(response.data);
            console.log(response.data);
        }
    }

    return (
        <div className="container-xxl py-5">
            <div className="container">
                <div className="text-center wow fadeInUp" data-wow-delay="0.1s">
                    <h6 className="section-title text-center text-primary text-uppercase">Our Rooms</h6>
                    <h1 className="mb-5">Explore Our <span className="text-primary text-uppercase">Rooms</span>
                    </h1>
                </div>
                <div className="row g-4">
                {data && data.map((entity) => (
                    <div key={entity.id} className="col-lg-4 col-md-6 wow fadeInUp" data-wow-delay="0.1s">
                        <div className="room-item shadow rounded overflow-hidden">
                            <div className="position-relative">
                                <img className="img-fluid" src={entity.main_image} alt=""/>
                                <small
                                    className="position-absolute start-0 top-100 translate-middle-y bg-primary text-white rounded py-1 px-3 ms-4">$100/Night</small>
                            </div>
                            <div className="p-4 mt-2">
                                <div className="d-flex justify-content-between mb-3">
                                    Type: <h5 className="mb-0">{entity.type.name}</h5>
                                    <div className="ps-2">
                                        <small className="fa fa-star text-primary"></small>
                                        <small className="fa fa-star text-primary"></small>
                                        <small className="fa fa-star text-primary"></small>
                                        <small className="fa fa-star text-primary"></small>
                                        <small className="fa fa-star text-primary"></small>
                                    </div>
                                </div>
                                <div className="d-flex mb-3">
                                    {entity.facilities.map((facility) => (
                                        <small key={entity.id} className="border-end me-3 pe-3"><i className="fa fa-bed text-primary me-2"></i>{facility.name}</small>
                                    ))}

                                </div>
                                <p className="text-body mb-3">Erat ipsum justo amet duo et elitr dolor, est duo
                                    duo eos lorem sed diam stet diam sed stet lorem.</p>
                                <div className="d-flex justify-content-between">
                                    <a className="btn btn-sm btn-primary rounded py-2 px-4" href="">View
                                        Detail</a>
                                    <a className="btn btn-sm btn-dark rounded py-2 px-4" href="">Book Now</a>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
                </div>
            </div>
        </div>

    );
}
