import React, {Dispatch, SetStateAction, useState} from 'react';
import './Dropdown.scss';

interface Entity {
    id: number;
    name: string;
}

interface DropdownProps {
    entities: Entity[];
    selectedEntity: Entity | null;
    setSelectedEntity: Dispatch<SetStateAction<Entity | null>>,
}

const Dropdown = ({ entities, selectedEntity, setSelectedEntity }: DropdownProps) => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    };

    const selectEntity = (entity: Entity) => {
        setSelectedEntity(entity);
        toggleDropdown();
    };

    return (
        <div className="dropdown">
            <div className="dropdown-toggle" onClick={toggleDropdown}>
                {selectedEntity ? selectedEntity.name : 'Select Entity'}
            </div>
            {isOpen && (
                <ul className="dropdown-menu">
                    {entities.map(entity => (
                        <li key={entity.id} onClick={() => selectEntity(entity)}>
                            {entity.name}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Dropdown;
