import React, {Dispatch, SetStateAction, useState} from 'react';
import "./style.sass"

interface Entity {
    id: number;
    name: string;
    description: string;
}

interface DropdownProps {
    entities: Entity[];
    selectedEntity: Entity | null;
    setSelectedEntity: Dispatch<SetStateAction<Entity | null>>,
}
const Dropdown = ({ entities, selectedEntity, setSelectedEntity }: DropdownProps) => {
    const [isOpen, setIsOpen] = useState(false);

    const handleClick = (selected: Entity) => {
        setSelectedEntity(selected);
        console.log(selected);
        setIsOpen(false);
    };

    return (

        <div className="dropdown">
            <button className="dropdown-toggle" onClick={toggleDropdown}>
                {selectedEntity ? selectedEntity.name : 'Select an Entity'}
            </button>
            {isOpen && (
                <ul className="dropdown-menu">
                    {entities.map((entity) => (
                        <li key={entity.id} onClick={() => handleSelect(entity)}>
                            {entity.name}
                        </li>
                    ))}
                </ul>
            )}
        </div>

    );
};

export default Dropdown;
