import React, {FC, ReactElement, useState} from "react";
import { Box, Typography, Stack, LinearProgress, Button, Alert, AlertTitle} from "@mui/material";
import {TitleField} from "../fieldComponents/titleField";
import {DescriptionField} from "../fieldComponents/descriptionField";
import {DateField} from "../fieldComponents/dateField";
import {SelectField} from "../fieldComponents/selectField";
import { useMutation } from "@tanstack/react-query";
import {sendApiRequest} from "../../helpers/sendApiRequest";

export const CreateRoleForm: FC = (): ReactElement => {
    const [tile, setTitle] = useState<string | undefined>(
        undefined,
    );
    const [descripiton, setDescription] = useState<string | undefined>(
        undefined,
    );

    const createRoleMutation = useMutation((data) => {
        sendApiRequest(
            'http://localhost:3200/roles',
            'POST',
            data
        )
    });

    return (
        <Box
            display={"flex"}
            flexDirection={"column"}
            alignItems={"flex-start"}
            width={"100%"}
            px={4}
            my={6}
            >

            <Alert
            severity={"success"}
            sx={{ width: '100%', marginBottom: '16px'}}
            >
                <AlertTitle>Success</AlertTitle>
                New role has been created successfully
            </Alert>

            <Typography mb={2} component={"h2"} variant={"h6"}>
                Create New Role
            </Typography>
            <Stack sx={{ width: '100%'}} spacing={2}>
                <TitleField
                    onChange={(e) => setTitle(e.target.value)}
                />
                <DescriptionField
                    onChange={(e) => setDescription(e.target.value)}
                />
                {/*<DateField/>*/}
                {/*<Stack*/}
                {/*    sx={{ width: '100%'}}*/}
