                {/*            value: "option1",*/}
                {/*            label: "option1",*/}
                {/*        },*/}
                {/*        {*/}
                {/*            value: "option2",*/}
                {/*            label: "option2",*/}
                {/*        },*/}
                {/*    ]}*/}
                {/*    />*/}
                {/*    <SelectField*/}
                {/*        label={"priority"}*/}
                {/*        name={"priority"}*/}
                {/*        items={[*/}
                {/*            {*/}
                {/*                value: "priority1",*/}
                {/*                label: "priority1",*/}
                {/*            },*/}
                {/*            {*/}
                {/*                value: "priority2",*/}
                {/*                label: "priority2",*/}
                {/*            },*/}
                {/*        ]}/>*/}

                {/*</Stack>*/}
                <Button variant={"contained"} size={"large"} fullWidth>
                    Create New Role
                </Button>
            </Stack>
        </Box>
    )
}
