import React, {FC, ReactElement} from "react";
import {AppBar} from "@mui/material";
import {Toolbar} from "@mui/material";
import useScrollTrigger from '@mui/material/useScrollTrigger';
import logo from '../../assets/logo.jpg';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Button from '@mui/material/Button';
import {defaultState} from "../../store/state/authSlice";
import {useNavigate} from "react-router-dom";
import {useDispatch} from "react-redux";
import {useAppSelector} from "../../store/hook";

interface Props {
    window?: () => Window;
    children: React.ReactElement;
}
function ElevationScroll(props: Props) {
    const { children } = props;
    const trigger = useScrollTrigger({
        disableHysteresis: true,
        threshold: 0
    });

    return React.cloneElement(children, {
        elevation: trigger ? 4 : 0,
    });
}


export const Header: FC = (): ReactElement =>{
    const { name } = useAppSelector((state) => state.auth);
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const signout = () => {
        localStorage.removeItem("token");
        navigate("/signin");
        dispatch(defaultState());
    };

    return(
        <ElevationScroll>
            <AppBar position={"fixed"} color={"primary"}>
                <Toolbar disableGutters>
                    <img alt={"image logo"} className={"logo"} src={logo}/>
                    <Tabs className={"tabContainer"}>
                        <Tab className={"tab"} label={"Home"}/>
                        <Tab label={"Users"}/>
                        <Tab label={"Rooms"}/>
                        <Tab label={"Roles"}/>
                    </Tabs>
                    <div>
                        User Name - {name}
                        <Button onClick={signout}>Signout</Button>
                    </div>
                    <Button className={"buttonLogin"} variant="outlined"  color={"secondary"}>SignIn</Button>
                    <Button className={"buttonLogin"} variant="outlined"  color={"secondary"}>SignUp</Button>

                </Toolbar>
            </AppBar>
        </ElevationScroll>
    )
}
