import React, { useState, useEffect, useRef } from "react";
import Glide from '@glidejs/glide'
import "./glideStyle.sass";
import axios from "axios";

interface Entity {
    id: string;
    number: string;
    price: number;
    size: number;
    availability: boolean;
    reserved:Date;
    main_image: string;
    type: {
        roleId: string;
        name: string;
    }
    facilities: Facility[];
}

interface Facility {
    id: string;
    name: string;
}


export const ImagesGlide:React.FC = ()  => {
    const [currentSlide, setCurrentSlide] = useState(0);
    const sliderRef = useRef<HTMLDivElement>(null);
    const [rooms, setRooms] = useState<Entity[]>([]);

    useEffect(() => {
        getRooms();
    }, []);

    const getRooms = async () => {
        const response = await axios.get("http://localhost:3300/rooms");
        if(response.status === 200){
            setRooms(response.data);
            console.log(response.data);
        }
    }


    useEffect(() => {
        let glide: Glide | null = null;
        if (sliderRef.current) {
            glide = new Glide(sliderRef.current, {
                type: 'carousel',
                perView: 3,
                focusAt: 'center',
                gap: 40,
                autoplay: 2000,
                breakpoints: {
                    1200:{
                        perView: 3
                    },
                    800:{
                        perView: 2
                    }
                }
            });
            glide.mount();
        }
        return () => {
            if (glide) {
                glide.destroy();
            }
        }
    }, []);

    const handleSlideChange = (newSlide: number) => {
        setCurrentSlide(newSlide);
    };



    return (
        <div className="images glide" style={{ textAlign: "center" }} >
            {/*<div className="glide__track" data-glide-el="track">*/}
            {/*    <ul className="glide__slides">*/}
            {/*        {rooms.map((room, index) => (*/}
            {/*            <li key={index} className="glide__slide">*/}
            {/*                <img*/}
            {/*                    src={`${room.main_image}`}*/}
            {/*                    alt=""*/}
            {/*                />*/}
            {/*                <div>{room.price}</div>*/}
            {/*            </li>*/}
            {/*        ))}*/}
            {/*    </ul>*/}
            {/*</div>*/}

            {/*<div className="glide__arrows" data-glide-el="controls">*/}
            {/*    <button*/}
            {/*        className="glide__arrow glide__arrow--left"*/}
            {/*        data-glide-dir="<"*/}
            {/*        onClick={() => handleSlideChange(currentSlide - 1)}*/}
            {/*        disabled={currentSlide === 0}*/}
            {/*    >*/}
            {/*        <i className="fas fa-arrow-left"></i>*/}
            {/*    </button>*/}
            {/*    <button*/}
            {/*        className="glide__arrow glide__arrow--right"*/}
            {/*        data-glide-dir=">"*/}
            {/*        onClick={() => handleSlideChange(currentSlide + 1)}*/}
            {/*        disabled={currentSlide === rooms.length - 1}*/}
            {/*    >*/}
            {/*        <i className="fas fa-arrow-right"></i>*/}
            {/*    </button>*/}
            {/*</div>*/}
        </div>
    );
}
