import React, {useEffect, useState} from 'react';
import "./signup.sass";
import axios from "axios";
import {toast} from "react-toastify";
import Dropdown from "../../components/dropdownComp/dropdown";




const initialState={
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    roleId: ""

}

interface Entity {
    id: number;
    name: string;
    description: string;
}

function List({ items, onEditItem }) {
    return (
        <ul>
            {items.map((item) => (
                <li key={item.id} onClick={() => onEditItem(item.id)}>
                    {item.name}
                </li>
            ))}
        </ul>
    );
}

