import React, {useState} from "react";
import "./style.sass"

interface Props {
    text: string;
    entity: Entity[];
}

interface Entity {
    id: number;
    name: string;
}




export const Dropdown: React.FC<Props> = ({ text, entity }) => {
    const [selectedItem, setSelectedItem] = useState(null);

    const [isOpen, setIsOpen] = useState(false);

    const handleItemSelection = (item: Entity) => {
        setSelectedItem(item);
    };

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className="dropdown">
            <button>
                {selectedItem ? selectedItem.name : 'Select an Item'}
                <i className="arrow down" />
            </button>
            <ul>
                {entity.map(item => (
                    <li key={item.id} onClick={() => handleItemSelection(item)}>
                        {item.name}
                    </li>
                ))}
            </ul>
        </div>
    );
};
