import React, { useState } from "react";
// import DateRangePicker from "react-daterange-picker";
// import "react-daterange-picker/dist/css/react-calendar.css";
// import "react-daterange-picker/dist/css/react-calendar-time.css";
import { Range } from 'react-date-range';
function DateRange() {
    const [dates, setDates] = useState(null);

    const handleSelect = (dates: any) => {
        setDates(dates);

        if (dates) {
            const startDate = dates.start.format("YYYY-MM-DD");
            const endDate = dates.end.format("YYYY-MM-DD");

            fetch("/home/review/viewAvailableRooms", {
                method: "POST",
                body: JSON.stringify({ startDate, endDate }),
                headers: {
                    "Content-Type": "application/json",
                },
            })
                .then((response) => response.json())
                .then((data) => {
                    let inner = "";
                    data.rooms.forEach((room: { main_image: any; name: any; cost: any; room_id: any; }) => {
                        inner += `
              <div class="col-xs-12 col-sm-4">
                <div class="card">
                  <a class="img-card" href="http://www.fostrap.com/2016/03/bootstrap-3-carousel-fade-effect.html">
                    <img src="http://localhost:5500/images/uploaded_images/${room.main_image}" />
                  </a>
                  <div class="card-content">
                    <h4 class="card-title">
                      <a href="http://www.fostrap.com/2016/03/bootstrap-3-carousel-fade-effect.html">${room.name}</a>
                    </h4>
                    <p class="">${room.cost} $/night</p>
                  </div>
                  <div class="card-read-more">
                    <a href="/home/reserve/${room.room_id}/${startDate}/${endDate}" class="btn btn-link btn-block">
                      Reserve
                    </a>
                  </div>
                </div>
              </div>
            `;
                    });
                    const element = document.getElementById("test");
                    if (element) {
                        element.innerHTML = inner;
                    }
                })
                .catch((error) => console.error("Error:", error));
        }
    };

    return (
        <div>
            <script type="text/javascript" src="https://cdn.jsdelivr.net/npm/daterangepicker/daterangepicker.min.js"></script>
            <link rel="stylesheet" type="text/css" href="https://cdn.jsdelivr.net/npm/daterangepicker/daterangepicker.css" />
            <h1 className="heading">Book a Room Now</h1>

            <div className="my-2 px-1">
                View available rooms&nbsp;
                <div className="my-2 px-1">
                    View available rooms  &nbsp;
                    <h1>Testt=</h1>
                    <input className="p-2" type="text" id="dateRangeInput" name="daterange" value=""/>

                </div>
                {/*<DateRangePicker*/}
                {/*    value={dates}*/}
                {/*    onSelect={handleSelect}*/}
                {/*    singleDateRange={true}*/}
                {/*/>*/}
            </div>

            <div className="container">
                <div className="row" id="test"></div>
            </div>
        </div>
    );
}

export default DateRange;
