import React, {useEffect, useState} from 'react';
import "./signup.sass";
import axios from "axios";
import {toast} from "react-toastify";
import Dropdown from "../../components/dropdownComp/dropdown";




const initialState={
    firstname: "",
    lastname: "",
    email: "",
    password: ""
}

interface Entity {
    id: number;
    name: string;
    description: string;
}

const Signup = () => {

    const [state, setState] = useState(initialState);
    const { firstname, lastname, email, password } = initialState;

    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const {name, value } = event.target;
        setState({...state, [name]:value})
    }


    // const [entities, setEntities] = useState<Entity[]>([]);
    const [selectedEntity, setSelectedEntity] = useState<Entity | null>(null);

    const entities: Entity[] = [
        { id: 1, name: 'Item 1', description: 'Description 1' },
        { id: 2, name: 'Item 2', description: 'Description 2' },
        { id: 3, name: 'Item 3', description: 'Description 3' }
    ];

    // useEffect(() => {
    //     axios.get('http://localhost:3300/roles').then(res => {
    //         setEntities(res.data);
    //     });
    // }, []);



    const addUser = async (data: {firstname: string; lastname: string; email:string; password:string}) => {
        console.log(data);
        const response = await axios.post("http://localhost:3300/signup", data);
        toast.success(response.data);
    }

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        addUser(state)
        // setSuccess(true);
        // window.location.reload()

    };




    // const handleSelect = (selected: Entity) => {
    //     console.log(selected);
    // };

    return (
      <div className="container-signup">
          {/*<p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>*/}
          <div className="card-signup">
              <div className="card-image">
                  <h2 className="card-heading">
                      Royal Hotel
                      <small>Royal Hotel</small>
                  </h2>
              </div>
              <form onSubmit={handleSubmit}>
                  <div className="card-form">
                      <div className="column">
                          <div className="input">
                              <input type="text" className="input-field"
                                     onChange={handleInputChange}
                                     required/>
                              <label className="input-label">Name</label>
                          </div>
                          <div className="input">
                              <input type="text" className="input-field"
                                     onChange={handleInputChange}
                                     required/>
                              <label className="input-label">Email</label>
                          </div>
                          <div className="input">
                              <input type="password" className="input-field"
                                     onChange={handleInputChange}
                                     required/>
                              <label className="input-label">Password</label>
                          </div>
                      </div>
                      <div className={"column"}>
                          <div className="input">
                              <input type="text" className="input-field"
                                     onChange={handleInputChange}
                                     required/>
                              <label className="input-label">Lastname</label>
                          </div>
                          <div className={"input"}>
                             {/*<Dropdown entity={entities}/>*/}
                              <Dropdown entities={entities} selectedEntity={selectedEntity} setSelectedEntity={setSelectedEntity} />
                          </div>
                          <div className="input">
                              <input type="password" className="input-field"
                                     onChange={handleInputChange}
                                     required/>
                              <label className="input-label">Password</label>
                          </div>
                      </div>


                  </div>
                      <div className="action">
                          <button  className="action-button">Get started</button>
                          {/*disabled={!validName || !validPwd || !validMatch ? true : false}*/}
                      </div>


              </form>
              <div className="card-info">
                  <p>By signing up you are agreeing to our <a href="#">Terms and Conditions</a></p>
              </div>
          </div>
      </div>

  );
};

export default Signup;
