import Profile from "../profile/profile";
import React, { useState, useEffect } from "react";
import {Button, Table, TableContainer, Tbody, Td, Th, Thead, Tr} from "@chakra-ui/react";
import "./style.sass";
import { addItem, getItems, updateItem, deleteItem } from "../../action";
import {Item} from "../../store/item";
import axios from "axios";
import {toast} from "react-toastify";

// const ItemContext = React.createContext<{
//     editingItemId: string | null;
//     onEditItem: (id: string) => void;
// }>({
//     editingItemId: null,
//     onEditItem: (id: string) => {
//         console.log(`Item with id ${id} is being edited`);
//     }
// });
// interface Props {
//     items: Item[];
//     addItem: typeof addItem;
//     getItems: typeof getItems;
//     updateItem: typeof updateItem;
//     deleteItem: typeof deleteItem;
// }
interface Entity {
    id: string;
    name: string;
    description: string;
}

interface ListProps {
    onEdit: (id: string) => void;
}

const EntityList: React.FC = ({ onEdit }: ListProps) => {
    // const [entities, setEntities] = useState<Entity[]>([]);

    // useEffect(() => {
    //     const fetchData = async () => {
    //         try {
    //             const response = await axios.get("http://localhost:3300/roles");
    //             const data = await response.json();
    //             setEntities(data as Entity[]);
    //         } catch (error) {
    //             console.error(error);
    //         }
    //     };
    //
    //     fetchData();
    // }, []);

    const [data, setData] = useState<Entity[]>([]);
    const [editingItemId, setEditingItemId] = useState<string | null>(null);

    const onEditItem = (id: string) => {
        setEditingItemId(id);
    };

    useEffect(() => {
        getRoles();
    }, []);

    const getRoles = async () => {
        const response = await axios.get("http://localhost:3300/roles");
        if(response.status === 200){
            setData(response.data);
        }
    }

    const onDeleteRole = async (id: string) => {
        if(window.confirm("Are you sure you want to delete this role")){
            const response = await axios.delete(`http://localhost:3300/roles/${id}`);
            if(response.status === 200){
                toast.success(response.data);
                getRoles();
            }
        }

    }

    console.log("data " + data);

    const [editId, setEditId] = useState<string | null>(null);

    const handleEdit = (id: string) => {
        setEditId(id);
    };

    return (
        <div className={"div-container"}>
            <TableContainer>
                <Table size={"sm"}>
                    <Thead bg={"burlywood"} className={"thead"}>
                        <Tr className={"tr"}>
                            <Th className={"th"}>Name</Th>
                            <Th className={"th"}>Description</Th>
                            <Th className={"th"}>Edit</Th>
                            <Th className={"th"}>Delete</Th>
                        </Tr>
                    </Thead>
                    <Tbody>
                        {data && data.map((entity) => (
                        <Tr className={"tr"} key={entity.id}>
                            <Td className={"td"}>{entity.name}</Td>
                            <Td className={"td"}>{entity.description}</Td>
                            <Td className={"td"}>
                                <Button onClick={() => handleEdit(entity.id)}  bg={"darkcyan"} className={"edit"}>Edit</Button>
                            </Td>
                            <Td className={"td"}>
                                <Button onClick={() => onDeleteRole(entity.id)} bg={"darkred"} className={"delete"}>Delete</Button>
                            </Td>
                        </Tr>
                        ))}
                    </Tbody>

                </Table>

            </TableContainer>

            <ul>
                <h1>test</h1>

            </ul>
        </div>

    );
};

export default EntityList;
