import { Grid, Heading, Stack } from "@chakra-ui/layout";
import { Form, Formik } from "formik";
import { InputControl, SubmitButton } from "formik-chakra-ui";
import { useSignupUserMutation } from "../../store/api/authApi";
import { useRef, useState, useEffect } from "react";
import "./signup.sass";

const USER_REGEX = /^[A-z][A-z0-9-_]{3,23}$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;
const REGISTER_URL = '/signup';
const Signup = () => {
  // const [signupUser, { data, isLoading }] = useSignupUserMutation();
  // console.log(data);

    const userRef = useRef();
    const errRef = useRef();

    const [user, setUser] = useState('');
    const [validName, setValidName] = useState(false);
    const [userFocus, setUserFocus] = useState(false);

    const [pwd, setPwd] = useState('');
    const [validPwd, setValidPwd] = useState(false);
    const [pwdFocus, setPwdFocus] = useState(false);

    const [matchPwd, setMatchPwd] = useState('');
    const [validMatch, setValidMatch] = useState(false);
    const [matchFocus, setMatchFocus] = useState(false);

    const [errMsg, setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);


    return (

      <div className="container-signup">
          <div className="card-signup">
              <div className="card-image">
                  <h2 className="card-heading">
                      Royal Hotel
                      <small>Royal Hotel</small>
                  </h2>
              </div>
              <form >
                  <div className="card-form">
                      <div className="column">
                          <div className="input">
                              <input type="text" className="input-field"  required/>
                              <label className="input-label">Name</label>
                          </div>
                          <div className="input">
                              <input type="text" className="input-field"  required/>
                              <label className="input-label">Email</label>
                          </div>

                      </div>
                      <div className={"column"}>
                          <div className="input">
                              <input type="text" className="input-field"  required/>
                              <label className="input-label">Lastname</label>
                          </div>
                          <div className="input">
                              <input type="password" className="input-field" required/>
                              <label className="input-label">Password</label>
                          </div>
                      </div>
                  </div>
                      <div className="action">
                          <button className="action-button">Get started</button>
                      </div>


              </form>
              <div className="card-info">
                  <p>By signing up you are agreeing to our <a href="#">Terms and Conditions</a></p>
              </div>
          </div>
      </div>

  );
};

export default Signup;
