import React, { FC, ReactElement, useState} from "react";
import { Grid, GridItem } from '@chakra-ui/react'


import {Header} from "../../components/header/header";
import RoleForm from "../../components/Forms/RoleForm";
import {DataCounter} from "../../components/dataCounter/dataCounter";
import EntityList from "../../components/formComponents/ListAllComponent";
import TypeForm from "../../components/Forms/TypeForm";

export const Type: FC = (): ReactElement => {
    const [selectedId, setSelectedId] = useState<string | null>(null);

    const handleEditRole = (id: string) => {
        setSelectedId(id);
    };

    return (
        <Grid
            h='500px'
            templateColumns={'65% 35%'}
            gap={4}
        >
            <GridItem colSpan={2}>
                <Header/>
            </GridItem>
            <GridItem rowSpan={2} bg='papayawhip'>
                <EntityList onEdit={handleEditRole} endpoint="http://localhost:3300/types"/>;
            </GridItem>
            <GridItem rowSpan={2} bg='papayawhip' >
                <TypeForm selectedTypeId={selectedId} onSave={() => setSelectedId(null)}/>
            </GridItem>

        </Grid>

    )
};
