import { addItem, getItems, updateItem, deleteItem } from "../../action";
import {Item} from "../../store/item";
import axios from "axios";
import { toast } from "react-toastify";
import {  useNavigate, useLocation } from "react-router-dom"

interface Props {
    items: Item[];
    addItem: typeof addItem;
    getItems: typeof getItems;
    updateItem: typeof updateItem;
    deleteItem: typeof deleteItem;
}

const initialState={
    name: "",
    description: ""
}

const MyForm = () => {

    const [state, setState] = useState(initialState);
    const {name, description} = initialState;

    const navigate = useNavigate();
    const addRole = async (data: { name: string; description: string; }) => {
        const response = await axios.post("http://localhost:3300/roles", data);
        toast.success(response.data);
    }

    // const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    //     e.preventDefault();
    // };


    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        // if(!name || !description){
        //     // alert("test");
        //     toast.error("Do not leave blank fields");
        // } else{
            alert("test");
            addRole(state);
            // navigate("/");
        // }
    };

    // const handleInputChange = (e: React.FormEvent<HTMLFormElement>) => {
    //     const {name, value } = e.target;
    //     setState({...state, [name]:value})
    //
    // }

    return (
        // <div className={"div-container"}>
        //     <TextComp text={"Create a new role"} />
            <form className={"form"}
                onSubmit={handleSubmit}
            >
                <label htmlFor={"name"}>Name</label>
                <input  name="name" id={"name"}  type={"text"} placeholder={"Enter name..."} />
                {/*<label htmlFor={"description"}>Description</label>*/}
                {/*<textarea id={"description"} name={"description"} placeholder={"Description..."} />*/}
                <input type={"submit"} value={"Create"}/>
            </form>
        // </div>

    );
};

export default MyForm;
