import React  from 'react';
import { Grid, Heading, Stack } from "@chakra-ui/layout";
import { Form, Formik } from "formik";
import { InputControl, SubmitButton } from "formik-chakra-ui";
import { useSignupUserMutation } from "../../store/api/authApi";
import { useRef, useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheck, faTimes, faInfoCircle } from "@fortawesome/free-solid-svg-icons";
import "./signup.sass";
import axios from "axios";
import {toast} from "react-toastify";

const USER_REGEX = /^[A-z][A-z0-9-_]{3,23}$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;
const REGISTER_URL = '/signup';

// interface User{
//     firstname: string;
//     lastname: string;
//     email: string;
//     password: string;
// }

const initialState={
    firstname: "",
    lastname: "",
    email: "",
    password: ""
}
interface User {
    data: any;
}

const Signup = () => {
  // const [signupUser, { data, isLoading }] = useSignupUserMutation();
  // console.log(data);

    // const userRef = useRef<HTMLInputElement>(null);
    // const errRef = useRef<HTMLInputElement>(null);
    // const [firstname, setFirstname] = useState('');
    // const [lastname, setLastname] = useState('');
    // const [email, setEmail] = useState('');
    // const [pwd, setPwd] = useState('');

    const [state, setState] = useState(initialState);
    const {firstname, lastname, email, password} = initialState;

    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const {name, value } = event.target;
        setState({...state, [name]:value})
    }



    // const [validName, setValidName] = useState(false);
    // const [userFocus, setUserFocus] = useState(false);


    // const [validPwd, setValidPwd] = useState(false);
    // const [pwdFocus, setPwdFocus] = useState(false);
    //
    // const [matchPwd, setMatchPwd] = useState('');
    // const [validMatch, setValidMatch] = useState(false);
    // const [matchFocus, setMatchFocus] = useState(false);
    //
    // const [errMsg, setErrMsg] = useState('');
    // const [success, setSuccess] = useState(false);

    // useEffect(() => {
    //     if (userRef.current) {
    //         userRef.current.focus();
    //     }
    // }, []);

    // useEffect(() => {
    //     setValidName(USER_REGEX.test(user));
    // }, [user])
    //
    // useEffect(() => {
    //     setValidPwd(PWD_REGEX.test(pwd));
    //     setValidMatch(pwd === matchPwd);
    // }, [pwd, matchPwd])
    //
    // useEffect(() => {
    //     setErrMsg('');
    // }, [user, pwd, matchPwd])
    //

    const addUser = async ({data}: User) => {
        console.log(data);
        const response = await axios.post("http://localhost:3300/signup", data);
        toast.success(response.data);
    }

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        console.log(firstname, lastname, email, password);
        addUser(state)
        // setSuccess(true);
        // window.location.reload()

    };

    return (
      <div className="container-signup">
          {/*<p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>*/}
          <div className="card-signup">
              <div className="card-image">
                  <h2 className="card-heading">
                      Royal Hotel
                      <small>Royal Hotel</small>
                  </h2>
              </div>
              <form onSubmit={handleSubmit}>
                  <div className="card-form">
                      <div className="column">
                          <div className="input">
                              <input type="text" className="input-field"
                                     onChange={handleInputChange}
                                     required/>
                              <label className="input-label">Name</label>
                              {/*<p id="uidnote" className={userFocus && user && !validName ? "instructions" : "offscreen"}>*/}
                              {/*    <FontAwesomeIcon icon={faInfoCircle} />*/}
                              {/*    4 to 24 characters.<br />*/}
                              {/*    Must begin with a letter.<br />*/}
                              {/*    Letters, numbers, underscores, hyphens allowed.*/}
                              {/*</p>*/}
                          </div>
                          <div className="input">
                              <input type="text" className="input-field"
                                     onChange={handleInputChange}
                                     required/>
                              <label className="input-label">Email</label>
                          </div>

                      </div>
                      <div className={"column"}>
                          <div className="input">
                              <input type="text" className="input-field"
                                     onChange={handleInputChange}
                                     required/>
                              <label className="input-label">Lastname</label>
                          </div>
                          <div className="input">
                              <input type="password" className="input-field"
                                     onChange={handleInputChange}
                                     required/>
                              <label className="input-label">Password</label>
                          </div>
                      </div>
                  </div>
                      <div className="action">
                          <button  className="action-button">Get started</button>
                          {/*disabled={!validName || !validPwd || !validMatch ? true : false}*/}
                      </div>


              </form>
              <div className="card-info">
                  <p>By signing up you are agreeing to our <a href="#">Terms and Conditions</a></p>
              </div>
          </div>
      </div>

  );
};

export default Signup;
