import React from "react";

interface Props {
    name: string;
    value: string;
    onChange: (event: React.ChangeEvent<HTMLSelectElement>) => void;
    entityList: {id: string, name: string}[];
    selectedId?: string | null;
}

const SelectInput: React.FC<Props> = ({name, value, onChange, entityList, selectedId = null }) => {
    return (
        <div className="select-box border bg-white">
            <div className="text-muted pl-1">{name}</div>
            <select  name={name} value={value} onChange={onChange}>
                <div className="text-muted pl-1">Photo Type</div>
                {entityList.map(entity => (
                    <option key={entity.id} value={entity.id}>
                        {entity.name}
                    </option>
                ))}
            </select>
        </div>
    );
};

export default SelectInput;
